import java.awt.Graphics;
import java.awt.Rectangle;
import java.awt.image.BufferedImage;
import java.io.ByteArrayInputStream;
import java.io.IOException;
import java.util.Base64;
import javax.imageio.ImageIO;

public class Bullet {
	
	private double x, y;
	private int direction;
	BufferedImage image = null;
	MainPanel mainpanel;
	
	public Rectangle getBounds() {
		return new Rectangle((int)x,(int)y, (int)(96*.15),(int)(96*.15));
	}
	
	public double getX() {
		return x;
	}

	public double getY() {
		return y;
	}

	public Bullet(double x, double y, MainPanel mainPanel, int direction ){
		this.x = x;
		this.y = y;
		this.mainpanel = mainPanel;
		this.direction = direction;
	
		try {
			image = ImageIO.read(ImageIO.createImageInputStream(new ByteArrayInputStream(Base64.getDecoder().decode(BULLET))));
		} catch (IOException e) {
			e.printStackTrace();
		}
	}
	
	public void tick() {
		if(!mainpanel.isPaused()) {
			if(direction == 1){
				x += 5;
			} else if(direction == 2) {
				x += 5;
				y -= 5;
			} else if(direction == 3) {
				y -= 5;
			} else if(direction == 4) {
				x -= 5;
				y -= 5;
			} else if(direction == 5) {
				x -= 5;
			} else if(direction == 6) {
				x -= 5;
				y += 5;
			} else if(direction == 7) {
				y += 5;
			} else if(direction == 8) {
				x += 5;
				y += 5;
			}
		}
	}
	
	public void draw(Graphics g) {
		g.drawImage(image,(int) x,(int) y, (int) (96*.15),(int) (96*.15),  null);
	}
		
	
	private static final String BULLET = "";

}
