import java.awt.Graphics;
import java.awt.Rectangle;
import java.awt.image.BufferedImage;
import java.io.ByteArrayInputStream;
import java.io.IOException;
import java.util.Base64;

import javax.imageio.ImageIO;

public class PowerUp {

	private double x, y;
	BufferedImage image = null;
	
	public Rectangle getBounds() {
		return new Rectangle(((int)x - 20), ((int)y - 20), 64, 64);
	}

	public double getX() {
		return x;
	}

	public double getY() {
		return y;
	}
	
	public PowerUp(int x, int y, MainPanel mainPanel) {
		this.x = x;
		this.y = y;
	
		try {
			image = ImageIO.read(ImageIO.createImageInputStream(new ByteArrayInputStream(Base64.getDecoder().decode(POWERUP))));
		} catch (IOException e) {
			e.printStackTrace();
		}
	}
	
	public void tick() {
		
	}
	
	public void draw(Graphics g) {
		g.drawImage(image,(int) x,(int) y, (int) (25),(int) (25),  null);
	}
	
	
	private static final String POWERUP = "";
}
